mapper.select:
select d.*, c.name as categoryName from dish d left outer join category c on d.category_id = c.id
and d.name like concat('%',#{name},'%') and d.category_id=#{categoryId} and d.status = #{status}
order by d.create_time desc

select d.*, c.name as categoryName
from dish d
left outer join category c on d.category_id = c.id

    and d.id = #{id}
    and d.name like concat('%',#{name},'%')
     and d.category_id=#{categoryId}
     and d.status=#{status}

order by create_time desc

mapper.update:
update dish
name=#{name}, category_id=#{categoryId}, price=#{price}, image=#{image}, description=#{description}, status=#{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}
